import React, { useState } from 'react';
import { useQuery } from 'react-query';
import axios from 'axios';

interface Account {
  id: number;
  accountNumber: string;
  accountType: string;
  balance: number;
  currency: string;
}

const Transfer: React.FC = () => {
  const [userId] = useState(2); // 仮のユーザーID
  const [formData, setFormData] = useState({
    fromAccountId: '',
    toAccountNumber: '',
    amount: '',
    description: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const { data: accounts, isLoading: accountsLoading } = useQuery(
    ['accounts', userId],
    async () => {
      const response = await axios.get(`/api/accounts/user/${userId}`);
      return response.data;
    }
  );

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      // 送金先の口座番号から口座IDを取得
      const toAccountResponse = await axios.get(`/api/accounts/number/${formData.toAccountNumber}`);
      const toAccountId = toAccountResponse.data.id;

      const response = await axios.post('/api/accounts/transfer', {
        fromAccountId: parseInt(formData.fromAccountId),
        toAccountId: toAccountId,
        amount: parseFloat(formData.amount),
      });

      if (response.data.message === '送金が完了しました') {
        setSuccess('送金が正常に完了しました');
        setFormData({
          fromAccountId: '',
          toAccountNumber: '',
          amount: '',
          description: '',
        });
      }
    } catch (err: any) {
      setError(err.response?.data?.error || '送金に失敗しました');
    } finally {
      setLoading(false);
    }
  };

  if (accountsLoading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-lg">読み込み中...</div>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto space-y-6">
      <div>
        <h1 className="text-3xl font-bold text-gray-900">振込</h1>
        <p className="text-gray-600">他の口座への振込を行います。</p>
      </div>

      <div className="bg-white shadow sm:rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* 送金元口座 */}
            <div>
              <label htmlFor="fromAccountId" className="block text-sm font-medium text-gray-700">
                送金元口座
              </label>
              <select
                id="fromAccountId"
                name="fromAccountId"
                required
                value={formData.fromAccountId}
                onChange={handleChange}
                className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-primary-500 focus:border-primary-500 sm:text-sm rounded-md"
              >
                <option value="">口座を選択してください</option>
                {accounts?.map((account: Account) => (
                  <option key={account.id} value={account.id}>
                    {account.accountNumber} - ¥{account.balance.toLocaleString()}
                  </option>
                ))}
              </select>
            </div>

            {/* 送金先口座番号 */}
            <div>
              <label htmlFor="toAccountNumber" className="block text-sm font-medium text-gray-700">
                送金先口座番号
              </label>
              <input
                type="text"
                name="toAccountNumber"
                id="toAccountNumber"
                required
                value={formData.toAccountNumber}
                onChange={handleChange}
                className="mt-1 focus:ring-primary-500 focus:border-primary-500 block w-full shadow-sm sm:text-sm border-gray-300 rounded-md"
                placeholder="123456789012"
              />
            </div>

            {/* 送金額 */}
            <div>
              <label htmlFor="amount" className="block text-sm font-medium text-gray-700">
                送金額
              </label>
              <div className="mt-1 relative rounded-md shadow-sm">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <span className="text-gray-500 sm:text-sm">¥</span>
                </div>
                <input
                  type="number"
                  name="amount"
                  id="amount"
                  required
                  min="1"
                  step="1"
                  value={formData.amount}
                  onChange={handleChange}
                  className="focus:ring-primary-500 focus:border-primary-500 block w-full pl-7 pr-12 sm:text-sm border-gray-300 rounded-md"
                  placeholder="0"
                />
              </div>
            </div>

            {/* 振込内容 */}
            <div>
              <label htmlFor="description" className="block text-sm font-medium text-gray-700">
                振込内容
              </label>
              <input
                type="text"
                name="description"
                id="description"
                value={formData.description}
                onChange={handleChange}
                className="mt-1 focus:ring-primary-500 focus:border-primary-500 block w-full shadow-sm sm:text-sm border-gray-300 rounded-md"
                placeholder="振込内容を入力してください"
              />
            </div>

            {error && (
              <div className="text-red-600 text-sm">{error}</div>
            )}

            {success && (
              <div className="text-green-600 text-sm">{success}</div>
            )}

            <div className="flex justify-end">
              <button
                type="submit"
                disabled={loading}
                className="bg-primary-600 border border-transparent rounded-md shadow-sm py-2 px-4 inline-flex justify-center text-sm font-medium text-white hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50"
              >
                {loading ? '送金中...' : '送金実行'}
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* 注意事項 */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-md p-4">
        <div className="flex">
          <div className="flex-shrink-0">
            <svg className="h-5 w-5 text-yellow-400" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
            </svg>
          </div>
          <div className="ml-3">
            <h3 className="text-sm font-medium text-yellow-800">注意事項</h3>
            <div className="mt-2 text-sm text-yellow-700">
              <ul className="list-disc pl-5 space-y-1">
                <li>送金先の口座番号は正確に入力してください</li>
                <li>送金手数料は無料です</li>
                <li>送金は即座に反映されます</li>
                <li>送金内容は取引履歴に記録されます</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Transfer;
