import React, { useState } from 'react';
import { useQuery } from 'react-query';
import axios from 'axios';

interface Account {
  id: number;
  accountNumber: string;
  accountType: string;
  balance: number;
  currency: string;
  status: string;
  interestRate: number;
  createdAt: string;
}

const Accounts: React.FC = () => {
  const [userId] = useState(2); // 仮のユーザーID

  const { data: accounts, isLoading, error } = useQuery(
    ['accounts', userId],
    async () => {
      const response = await axios.get(`/api/accounts/user/${userId}`);
      return response.data;
    }
  );

  const getAccountTypeLabel = (type: string) => {
    switch (type) {
      case 'SAVINGS':
        return '普通預金';
      case 'CHECKING':
        return '当座預金';
      case 'FIXED_DEPOSIT':
        return '定期預金';
      default:
        return type;
    }
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'ACTIVE':
        return '有効';
      case 'SUSPENDED':
        return '停止中';
      case 'CLOSED':
        return '解約済み';
      default:
        return status;
    }
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-lg">読み込み中...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center">
        <div className="text-red-600">エラーが発生しました</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-3xl font-bold text-gray-900">口座情報</h1>
        <p className="text-gray-600">お客様の口座一覧をご確認いただけます。</p>
      </div>

      <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
        {accounts?.map((account: Account) => (
          <div key={account.id} className="bg-white overflow-hidden shadow rounded-lg">
            <div className="p-6">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <div className="h-12 w-12 rounded-full bg-primary-100 flex items-center justify-center">
                    <span className="text-primary-600 font-bold text-lg">
                      {account.accountType.charAt(0)}
                    </span>
                  </div>
                </div>
                <div className="ml-4 flex-1">
                  <h3 className="text-lg font-medium text-gray-900">
                    {getAccountTypeLabel(account.accountType)}
                  </h3>
                  <p className="text-sm text-gray-500">
                    {account.accountNumber}
                  </p>
                </div>
              </div>

              <div className="mt-6">
                <div className="flex justify-between items-center mb-2">
                  <span className="text-sm font-medium text-gray-500">残高</span>
                  <span className="text-lg font-bold text-gray-900">
                    ¥{account.balance.toLocaleString()}
                  </span>
                </div>
                <div className="flex justify-between items-center mb-2">
                  <span className="text-sm font-medium text-gray-500">通貨</span>
                  <span className="text-sm text-gray-900">{account.currency}</span>
                </div>
                <div className="flex justify-between items-center mb-2">
                  <span className="text-sm font-medium text-gray-500">金利</span>
                  <span className="text-sm text-gray-900">
                    {(account.interestRate * 100).toFixed(2)}%
                  </span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm font-medium text-gray-500">ステータス</span>
                  <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                    account.status === 'ACTIVE' ? 'bg-green-100 text-green-800' :
                    account.status === 'SUSPENDED' ? 'bg-yellow-100 text-yellow-800' :
                    'bg-red-100 text-red-800'
                  }`}>
                    {getStatusLabel(account.status)}
                  </span>
                </div>
              </div>

              <div className="mt-6 pt-4 border-t border-gray-200">
                <div className="text-xs text-gray-500">
                  開設日: {new Date(account.createdAt).toLocaleDateString('ja-JP')}
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {accounts?.length === 0 && (
        <div className="text-center py-12">
          <div className="text-gray-500">口座が登録されていません</div>
        </div>
      )}
    </div>
  );
};

export default Accounts;
